package CollectionDemo;

public class Emp {
	private int empID;

	public Emp(int i) 
	{
		super();
		this.empID = empID;
	}

	public int getEmpID() {
		return empID;
	}

	public void setEmpID(int empID) 
	{
		this.empID = empID;
	}

	public void showEmpID() {
		System.out.println(+empID );
		
		// TODO Auto-generated method stub
		
	}

	@Override
	public String toString() {
		return "Emp [empID=" + empID + "]";
	}
	
	
	
}
